INSERT INTO `base1_hero` (`id`, `pic_id`, `model_id`, `is_cityshow`, `ingroup`, `pro_type`, `attack_type`, `race_type`, `flag_type`, `position_type`, `name`, `sex`, `introduce`, `info`, `star`, `culture`, `need_ston`, `hero_ext_id`, `is_show`, `show_position`, `is_open`, `star_lv`, `aptitude`, `simple_introdution`) VALUES
(1, '', '1', 1, 0, 2, 2, 1, 1, 2, '小黑', 2, '隐匿阴影的弓箭手。@@在不经意的时刻给予敌人索命一击的精湛箭术。@@她的箭雨是所有被她盯上敌人的挽歌。', '无', 3, 'ad:1:0.9,ae:1:3.3,af:1:0.8', 606, 1, 1, '0:0:0', 1, 25, 2, '沉默、减速、高伤害'),
(2, '', '2', 1, 0, 2, 1, 7, 2, 2, '巨魔战将', 1, '狂暴型的双斧战士@@物理中排输出@@强大的咒术能大幅度强化我方英雄的攻击能力', '无', 3, 'ad:1:2,ae:1:3,af:1:1', 607, 13, 1, '0:0:0', 1, 25, 3, '减速、减命中、高伤害'),
(3, '', '3', 1, 0, 3, 2, 1, 1, 2, '风行者', 2, '风神庇佑的弓箭手。@@行动如风一般迅捷，难以捕捉行动轨迹。@@她的箭矢可以停住敌人的脚步，也可以杀伤任何瞄准的目标。', '无', 3, 'ad:1:1,ae:1:1.8,af:1:2.2', 608, 25, 1, '0:0:0', 1, 25, 2, '高闪避、高伤害'),
(4, '', '4', 1, 0, 3, 2, 3, 0, 3, '冰女', 2, '寒冰的控制者，冷酷的冰雪之女。@@控制暴风雪无情掩埋敌人的元素法师。@@她手中的冰雪在杀伤敌人的同时能够冻结战场，阻碍敌人的行动。', '无', 4, 'ad:1:0.5,ae:1:1,af:1:1.5', 609, 37, 1, '0:0:0', 1, 25, 1, '控制、群体伤害'),
(5, '', '5', 1, 0, 3, 2, 3, 0, 2, '火女', 2, '高输出控制型法师@@通过火焰可使敌人眩晕@@狂暴状态下可以给敌人造成大范围的强力伤害', '无', 3, 'ad:1:1.1,ae:1:1.2,af:1:3.7', 610, 49, 1, '0:0:0', 1, 25, 3, '控制、群体伤害'),
(6, '', '6', 1, 0, 3, 2, 3, 2, 2, '宙斯', 1, '手握雷神之锤的王者。@@以手中武器号令雷电，御使强力的元素之力将敌人完全清扫。', '无', 4, 'ad:1:1.6,ae:1:1.4,af:1:3', 611, 61, 1, '0:0:0', 1, 25, 3, '高爆发、群体伤害'),
(7, '', '7', 1, 0, 1, 1, 2, 0, 1, '刚被兽', 1, '来自兽人族的战士，拥有坚实的肉体。@@能运用自身的能力形成坚固的壁垒。@@同时也能利用天赋技能干扰对手的行动。', '无', 5, 'ad:1:3.5,ae:1:2.3,af:1:1.2', 612, 73, 1, '0', 1, 25, 4, '强力肉盾'),
(8, '', '8', 1, 0, 1, 1, 3, 0, 3, '全能骑士', 2, '信仰千年者的强大骑士。@@能用“神”之力保护自己和同伴。@@同时也能运用这种力量治疗自己和同伴的身体。', '无', 3, 'ad:1:2.2,ae:1:1,af:1:1.8', 613, 85, 1, '0', 1, 25, 2, '回血、肉盾'),
(9, '', '9', 1, 0, 1, 1, 3, 0, 1, '船长', 1, '热血的海上男儿，战场上可靠的壁垒。@@有效抵御敌人攻击的同时也精于战术。@@对战场拥有绝佳的控制能力。', '无', 3, 'ad:1:2.5,ae:1:1.2,af:1:1.3', 614, 97, 1, '0', 1, 25, 2, '控制、肉盾'),
(10, '', '10', 1, 0, 3, 2, 1, 0, 3, '沉默术士', 2, '寡言少语的女性术士。@@法术代替了她的语言犹如天生能力一般使用自如。@@同时她也是场上所有法系对手的克星，只要一个指令，就能让对方无法发挥实力。', '无', 3, 'ad:1:0.7,ae:1:1.7,af:1:2.6', 615, 109, 1, '0', 1, 25, 2, '沉默、高伤害'),
(11, '', '11', 1, 0, 1, 1, 2, 2, 1, '斧王', 1, '使用大斧犹如手臂的强力战士。@@传承者中优秀的实力者，是同伴们坚固的盾牌。@@同时对于敌人，他也是恐怖的杀戮机器。', '无', 4, 'ad:1:3.3,ae:1:1.2,af:1:1.5', 616, 121, 1, '0', 1, 25, 4, '控制、肉盾'),
(12, '', '12', 1, 0, 2, 2, 9, 0, 1, '矮人火枪', 2, '“我已经瞄准了你的脑袋。”@@远距离型物理输出，单体攻击力极高。@@往往可以在一次攻击后扭转整个局势。', '无', 5, 'ad:1:1,ae:1:4.2,af:1:1.8', 617, 134, 1, '0', 1, 25, 4, '超强输出'),
(13, '', '13', 1, 0, 2, 2, 1, 1, 3, '白虎', 2, '野兽的敏捷与弓手的精准融于一身。@@与白虎相伴的弓箭手，拥有月神赐福之箭。@@她的箭能够束缚对手的行动，同时也能够呼唤星辰降临杀伤所有进攻她的敌人。', '无', 4, 'ad:1:1.3,ae:1:3,af:1:2.7', 618, 146, 1, '0', 1, 25, 4, '控制、高伤害'),
(14, '', '14', 1, 0, 3, 2, 1, 0, 3, '小鹿', 2, '美丽的森林之女。@@用自然的眷顾抚慰队友的创伤。@@她的美丽甚至能诱惑对手的心，让敌方产生混乱。', '无', 4, 'ad:1:0.8,ae:1:1.6,af:1:3.6', 619, 157, 1, '0', 1, 25, 3, '强力回血'),
(15, '', '15', 1, 0, 3, 1, 8, 0, 1, '蓝胖', 1, '集近战和施法者一体的食人魔法师@@有着优秀的施法能力的同时作为肉盾更加合格。@@当他站在第一线的时候，小心他的火球。', '无', 2, 'ad:1:1.6,ae:1:0.7,af:1:1.7', 620, 169, 1, '0', 1, 25, 1, '控制、辅助'),
(16, '', '16', 1, 0, 3, 2, 5, 0, 1, '死亡先知', 2, '作为一名女性法师，她最得意的技能是操控死亡。@@在战场上玩弄着亡灵法术和沉默的她，是告知死亡的真正先知。', '无', 3, 'ad:1:1.5,ae:1:1.3,af:1:3.2', 621, 182, 1, '0', 1, 25, 3, '沉默、群体伤害'),
(17, '', '17', 1, 0, 2, 2, 5, 0, 1, '电魂', 1, '“所有人都认为我该站在背后，但前方才是我的位置。”@@高输出型坦克，作为施法者却拥有出色的反弹伤害能力。', '无', 4, 'ad:1:2,ae:1:2.3,af:1:1.7', 622, 193, 1, '0', 1, 25, 3, '高伤害'),
(18, '', '18', 1, 0, 3, 2, 5, 0, 3, '瘟疫法师', 1, '灾难在世界上的化身。@@夺取他人的力量恢复己方战力的极端术者。@@“别人的血肉就是我的粮食。”', '无', 4, 'ad:1:2,ae:1:1.6,af:1:3.4', 623, 206, 1, '0', 1, 25, 4, '回血、高爆发'),
(19, '', '19', 1, 0, 1, 1, 5, 0, 1, '骷髅王', 1, '模糊了生存和死亡界限的王者。@@没有痛觉不会死亡的身躯让他站在最前线抵挡对手的攻击。@@而夺取他人血液恢复自身和重生的能力，则让他成为战场上不灭的梦魇。', '无', 4, 'ad:1:2.5,ae:1:2.3,af:1:1.2', 624, 218, 1, '0', 1, 25, 3, '肉盾'),
(20, '', '20', 1, 0, 3, 2, 2, 0, 3, '恶魔巫师', 1, '“战斗的精髓在于控制而非绝对的伤害”@@出生于兽人却更明白施法艺术的恶魔巫师是绝佳的辅助者。@@他的每一个技能都有可能改变战斗的进程。', '无', 2, 'ad:1:0.6,ae:1:0.9,af:1:2.5', 625, 229, 1, '0', 1, 25, 1, '控制'),
(81, '', '81', 1, 0, 2, 1, 2, 0, 2, '剑圣', 1, '“我的剑无坚不摧”@@来自兽族的剑圣，既拥有恐怖的输出,同时也能抚慰队友的@@伤口。', '无', 4, 'ad:1:1.8,ae:1:3.8,af:1:1.4', 1040, 262, 1, '0', 1, 25, 4, '高爆发、回血'),
(85, '', '85', 1, 0, 2, 2, 5, 3, 2, '冥界亚龙', 1, '来自异界的亚龙，拥有强力的输出能力。@@拥有令敌人颤栗的毒素，每次攻击都使对手麻痹，对手慢慢死@@于亚龙的毒液中。', '无', 4, 'ad:1:1.5,ae:1:3,af:1:2.5', 1041, 274, 1, '0', 1, 25, 4, '减速、高伤害'),
(89, '', '89', 1, 0, 1, 1, 6, 0, 3, '流浪剑客', 1, '拥有异常强大力量的剑士。@@流浪剑客一边流浪一边修炼剑术，使得自身力量能瞬间爆发出来。', '无', 3, 'ad:1:1.6,ae:1:1.75,af:1:2.35', 1042, 288, 1, '0', 0, 25, 3, '控制、高伤害'),
(97, '', '97', 1, 0, 1, 1, 5, 0, 2, '死亡骑士', 1, '拥有顽强生命力的肉盾。@@死亡骑士原本就为圣骑士，堕落黑暗之后，肉体慢慢腐烂，骨@@头变得更加强壮了，几乎变得不死状态。', '无', 3, 'ad:1:1.6,ae:1:1.75,af:1:2.35', 1043, 300, 1, '0', 0, 25, 2, '肉盾'),
(95, '', '95', 1, 0, 1, 1, 4, 0, 3, '大鱼人', 1, '“我只为海妖族战斗”@@拥有高输出，团体控制的大鱼人向女妖王宣誓自己的忠诚。', '无', 3, 'ad:1:2.6,ae:1:1.3,af:1:1.1', 1044, 313, 1, '0', 0, 25, 2, '控制'),
(93, '', '93', 1, 0, 1, 1, 4, 0, 3, '末日使者', 1, '“请接受末日的审判吧！”@@出生于高贵的恶魔一族，末日使者掌控了末日的审判技能。', '无', 3, 'ad:1:3,ae:1:1.4,af:1:1.6', 1292, 326, 1, '0', 1, 25, 2, '半肉、高伤害、沉默'),
(99, '', '99', 1, 0, 1, 1, 4, 0, 3, '双头龙', 1, '罕见的冰火双修的奇美拉@@双头龙既拥有不俗的输出，同时他的寒冰之力也能将对手冰封@@起来。', '无', 3, 'ad:1:1.6,ae:1:1.75,af:1:2.35', 1120, 339, 1, '0', 0, 25, 2, '控制'),
(101, '', '101', 1, 0, 1, 1, 4, 0, 3, '术士', 1, '掌控火系禁术的术士。@@术士常年修习禁术，掌握了召唤地狱火术，令敌人闻风丧胆。', '无', 3, 'ad:1:1.6,ae:1:1.75,af:1:2.35', 1121, 352, 1, '0', 0, 25, 2, '高伤害');
